          filteredExpenses.map((
            expense // dynamic function for dynamic lists
          ) => (
            <ExpenseItem
              key={expense.id} //helps react identify using unique key
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          ))
        }
      </Card>
    </div>
  );
}

export default Expenses;
